import './CurrentWeather.css';
import one_d from './icons/01d.png';
import one_n from './icons/01n.png';
import two_d from './icons/02d.png';
import two_n from './icons/02n.png';
import three_d from './icons/03d.png';
import three_n from './icons/03n.png';
import four_d from './icons/04d.png';
import four_n from './icons/04n.png';
import nine_d from './icons/09d.png';
import nine_n from './icons/09n.png';
import ten_d from './icons/10d.png';
import ten_n from './icons/10n.png';
import eleven_d from './icons/11d.png';
import eleven_n from './icons/11n.png';
import twelve_d from './icons/12d.png';
import twelve_n from './icons/12n.png';
import thirteen_d from './icons/13d.png';
import thirteen_n from './icons/13n.png';

import unknown from './icons/';

const CurrentWeather = ({ data }) => {

    if (!data) {
        return null;
    }

    const getImagePath = (imageName) => {
        return images[imageName];
    };

    return (
        <div className='weather'>
            <div className='top'>
                <div>
                    <p className='city'>{data.city}</p>
                    <p className='weather-description'>{data.weather[0].description}</p>
                </div>
                <img
                    src={getImagePath(data.weather[0].icon)}
                    className='weather-icon'
                    alt='weather' />
            </div>
            <div className='bottom'>
                <p className='temperature'>{Math.round(data.main.temp)}Â°C</p>
                <div className='details'>
                    <div className='parameter-row'>
                        <span className='parameter-label'>Details: </span>
                    </div>
                    <div className='parameter-row'>
                        <span className='parameter-label'>Feels like: </span>
                        <span className='parameter-value'>{Math.round(data.main.feels_like)}Â°C</span>
                    </div>
                    <div className='parameter-row'>
                        <span className='parameter-label'>Wind: </span>
                        <span className='parameter-value'>{data.wind.speed} m/s</span>
                    </div>
                    <div className='parameter-row'>
                        <span className='parameter-label'>Humidity: </span>
                        <span className='parameter-value'>{data.main.humidity}%</span>
                    </div>
                    <div className='parameter-row'>
                        <span className='parameter-label'>Pressure: </span>
                        <span className='parameter-value'>{data.main.pressure} hPa</span>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CurrentWeather;
